    private void RdoVacasionesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RdoVacasionesActionPerformed
        CheckConferencia.setEnabled(false);
        CmbNinios.setEnabled(true);
    }//GEN-LAST:event_RdoVacasionesActionPerformed

    private void RdoNegociosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RdoNegociosActionPerformed
        CheckConferencia.setEnabled(true);
        CmbNinios.setEnabled(false);
    }//GEN-LAST:event_RdoNegociosActionPerformed

    private void BtnConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnConfirmarActionPerformed
        int habitacion=Integer.parseInt(TxtHabitacion.getText());
        int cantDias=CmbDias.getSelectedIndex();
        boolean vigente=CheckVigente.isSelected();
         if(RdoVacasiones.isSelected())
         {
             int ninios=CmbNinios.getSelectedIndex();
         
          ReservaVacasiones nuevaVacasion= new ReservaVacasiones( habitacion,cantDias,vigente,ninios);
          nuevaReserva.agregarReserva(nuevaVacasion);
         }
         else
         {
             boolean conferencia= CheckConferencia.isSelected();
             ReservaNegocios nuevaNegocios = new ReservaNegocios(habitacion,cantDias,vigente,conferencia);
             nuevaReserva.agregarReserva(nuevaNegocios);
         }
         LstListado.setListData(nuevaReserva.getReservas().toArray());
         this.habilitarControles();
    }//GEN-LAST:event_BtnConfirmarActionPerformed

    private void BtnReportesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnReportesActionPerformed
       
        TxtFacturacionTotal.setText(String.valueOf(nuevaReserva.facturacionTotal()));
        
        int[]auxiliar= nuevaReserva.cantReservasXtipo();
        for (int i = 0; i < auxiliar.length; i++) {
            TxtReservasVacasiones.setText(String.valueOf(auxiliar[0]));
            break;
        }
        for (int i = 0; i < auxiliar.length; i++) {
            TxtReservasNegocios.setText(String.valueOf(auxiliar[1]));
            break;
        }
        
        
    }//GEN-LAST:event_BtnReportesActionPerformed

    
    public void habilitarControles(){
    RdoVacasiones.setSelected(false);
    RdoNegocios.setSelected(false);
    TxtHabitacion.setText("");
    CmbDias.setSelectedIndex(0);
    CheckVigente.setSelected(false);
    CmbNinios.setSelectedIndex(0);
   
    CheckConferencia.setSelected(false);
    
    BtnReportes.setEnabled(true);
    }
    
    
    
    
    
